select.if:
select distinct o.*, d.fd_order_person, d.fd_supplier_name from cy_order o left join MTsupplier.dbo.cy_order_detailed d on o.fd_id = d.fd_parent_id where 1 = 1
and d.fd_quotation_tatus = #{fd_quotation_tatus} and o.fd_supplier_name = #{fd_supplier_name}
order by o.fd_no desc

mapper.select:
select cy_order_detailed.*, ComputationUnit.cComUnitName from cy_order_detailed left join [UFDATA_001_2015].[dbo].ComputationUnit on cy_order_detailed.fd_unit = ComputationUnit.cComunitCode where fd_parent_id = #{fd_parent_id} and cy_order_detailed.fd_quotation_tatus = #{fd_quotation_tatus}
and #{fd_order_person} in (select fd_order_person from cy_order_detailed where fd_parent_id = #{fd_parent_id})
and #{fd_supplier_name} in (select fd_supplier_name from MTsupplier.dbo.cy_order where cy_order.fd_id = #{fd_parent_id})

select fd_arrival_certificate
from cy_supplier_deliver_details
where fd_id = #{fd_id}
