mapper.select:
select *
from cy_supplier
        where 1 = 1

    and ID = #{ID}


    and suppliername = #{suppliername}


    and type = #{type}

mapper.update:
update cy_supplier
set startdate=#{startdate},
    enddate=#{enddate}
where ID = #{ID}
  and suppliername = #{suppliername}
  and suppliercode = #{suppliercode}

mapper.delete:
delete
from cy_supplier
where ID = #{ID}
  and suppliername = #{suppliername}
  and suppliercode = #{suppliercode}
